import React, { useState, useEffect } from "react";
import { Button, Alert } from "react-bootstrap";
import { useParams } from "react-router-dom";
import axios from "axios";

const RoomPage = () => {
    const { buildingId } = useParams();
    const [temperature, setTemperature] = useState(null);
    const [building, setBuilding] = useState(null);

    useEffect(() => {
        // Fetch the temperature from the server
        axios.get('http://localhost:5002/getTemp')
            .then(response => {
                setTemperature(response.data.temp);
            })
            .catch(error => {
                console.error('Error fetching temperature:', error);
            });

        // Fetch the building data from the server
        axios.get(`http://localhost:5002/buildings/${buildingId}`)
            .then(response => {
                setBuilding(response.data);
            })
            .catch(error => {
                console.error('Error fetching building:', error);
            });
    }, [buildingId]);

    return (
        <div style={{ padding: '20px', maxWidth: '800px', margin: 'auto' }}>
            <h2>Room Page</h2>
            {building && (
                <>
                    <h5>Building ID: {building._id}</h5>
                    <h3>Current Temperature: {temperature !== null ? `${temperature}°C` : "Loading..."}</h3>
                    {temperature > building.temperatureLimit && (
                        <Alert variant="danger">
                            Warning: Temperature exceeds the limit of {building.temperatureLimit}°C!
                        </Alert>
                    )}
                    <Button onClick={() => window.location.reload()}>Refresh Temperature</Button>
                </>
            )}
        </div>
    );
};

export default RoomPage;
