import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BuildingsPage from './pages/Buildings';
import RegistrationPage from './pages/Registration';
import LoginPage from './pages/Login';
import Navigation from './components/Navigation';
import {jwtDecode} from 'jwt-decode'; // Correct import for jwt-decode
import RoomPage from './pages/Room';

function App() {
  const [user, setUser] = useState(null);

  // Check for token in localStorage on app load to persist user session
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedUser = jwtDecode(token); // Use jwtDecode instead of jwt_decode
      setUser(decodedUser);
    }
  }, []);

  return (
    <Router>
      <div className="App">
        <Navigation user={user}/>
        <Routes>
          <Route path="/registration" element={<RegistrationPage setUser={setUser} />} />
          <Route path="/login" element={<LoginPage setUser={setUser} />} />
          <Route path="/" element={<BuildingsPage user={user} />} />
          <Route path="/buildings/:buildingId" element={<RoomPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
