// CreateBuilding.js

import React, { useState } from 'react';

const CreateBuilding = ({ handleCreateBuilding }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [temperatureLimit, setTemperatureLimit] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newBuilding = {
      id: Math.random(), // уникальный ID, лучше заменить на более надежный способ генерации ID
      name,
      description,
      temperatureLimit: parseFloat(temperatureLimit)
    };
    handleCreateBuilding(newBuilding); // Передаем новый объект здания в обработчик
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Building Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <input
        type="number"
        placeholder="Temperature Limit"
        value={temperatureLimit}
        onChange={(e) => setTemperatureLimit(e.target.value)}
        required
      />
      <button type="submit">Create Building</button>
    </form>
  );
};

export default CreateBuilding;
