// Buildings.js
import React, { useState } from "react";
import { Button, Card, Modal, Form, Row, Col } from "react-bootstrap";
import CreateBuilding from "../components/CreateBuilding";
import { Link } from "react-router-dom";
import Buildings from "../building_data.json";

const Home = () => {
  const [buildings, setBuildings] = useState(Buildings);
  const [filter] = useState("all");
  const [showCreateModal, setShowCreateModal] = useState(false);

  // Объявление функции handleCreateBuilding
  const handleCreateBuilding = (newBuilding) => {
    setBuildings([...buildings, newBuilding]);
    setShowCreateModal(false);
  };

  return (
    <div style={{ background: "#40E0D0", padding: "20px", minHeight: "100vh" }}>
      <h2 className="mb-4">Home Page</h2>
      <Button
        variant="light"
        style={{ fontSize: "1.1rem", padding: "12px 14px" }}
        onClick={() => setShowCreateModal(true)}
        className="mt-3"
      >
        Create Building
      </Button>

      <CreateBuilding
        show={showCreateModal}
        handleClose={() => setShowCreateModal(false)}
        handleCreate={handleCreateBuilding}
      />

      {/* Отображение существующих зданий */}
      <Row>
        {buildings.map((building) => (
          <Col key={building.id} lg={4} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{building.name}</Card.Title>
                <Card.Text>{building.description}</Card.Text>
                <Link to={`/building/${building.id}`}>
                  <Button variant="info" className="ml-2">
                    Detail
                  </Button>
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Home;
