import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const CreateBuildingForm = ({ show, handleClose, handleCreateBuilding }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [temperatureLimit, setTemperatureLimit] = useState('');

  const handleSubmit = () => {
    handleCreateBuilding({ name, description, temperatureLimit: parseFloat(temperatureLimit) });
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Create Building</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formBuildingName">
            <Form.Label>Building Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter building name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>

          <Form.Group controlId="formBuildingDescription">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>

          <Form.Group controlId="formTemperatureLimit">
            <Form.Label>Temperature Limit</Form.Label>
            <Form.Control
              type="number"
              placeholder="Enter temperature limit"
              value={temperatureLimit}
              onChange={(e) => setTemperatureLimit(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateBuildingForm;
