import React, { useState, useEffect } from "react";
import { Button, ListGroup } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Buildings from '../building_data.json';
import Rooms from '../room_data.json';
import CreateRoomForm from '../components/CreateRoomForm';
import RoomDetail from '../components/RoomDetail';

const RoomPage = () => {
  const { buildingId } = useParams();
  const selectedBuilding = Buildings.find((building) => building.id === parseInt(buildingId));
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showRoomDetail, setShowRoomDetail] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [rooms, setRooms] = useState([]);
  const [selectedState, setSelectedState] = useState('all');
  const [selectedPriority, setSelectedPriority] = useState('all');
  const [loading, setLoading] = useState(true);
  const [temperature, setTemperature] = useState(null);

  useEffect(() => {
    console.log("Fetching temperature...");

    fetch('http://localhost:5002/getTemp') // Убедитесь, что URL правильный
      .then(response => {
          console.log("Response received:", response);
          if (!response.ok) {
              throw new Error('Network response was not ok');
          }
          const contentType = response.headers.get("content-type");
          console.log("Content-Type:", contentType);
          if (contentType && contentType.indexOf("application/json") !== -1) {
              return response.json();
          } else {
              throw new Error("Received content is not JSON");
          }
      })
      .then(data => {
          console.log("Temperature data fetched:", data);
          setTemperature(data.temp);
      })
      .catch(error => {
          console.error('Error fetching temperature:', error.message);
          setTemperature("Error");
      });
}, []);


  const handleSetTemperature = (newTemp) => {
    fetch('/setTemp', {
      method: 'POST',
      headers: {
        'Authorization': 'Basic ' + btoa('admin:admin'), // Replace with secure credentials or prompt user
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ temp: newTemp }),
    })
    .then(response => response.json())
    .then(data => setTemperature(data.temp))
    .catch(error => console.error('Error setting temperature:', error));
  };

  const handleCreateRoom = (newRoom) => {
    setRooms([...rooms, newRoom]);
    setShowCreateModal(false);
  };

  const handleRoomDetail = (room) => {
    setSelectedRoom(room);
    setShowRoomDetail(true);
  };

  const handleCloseRoomDetail = () => {
    setShowRoomDetail(false);
    setSelectedRoom(null);
  };

  const handleDeleteRoom = (roomId) => {
    const updatedRooms = rooms.filter((room) => room.id !== roomId);
    setRooms(updatedRooms);
    setSelectedRoom(null);
    setShowRoomDetail(false);
  };

  const handleEditRoom = (room) => {
    setSelectedRoom(room);
    setSelectedState(room.state);
    setSelectedPriority(room.priority);
    setShowCreateModal(true);
  };

  const handleUpdateRoom = (updatedRoom) => {
    const updatedRooms = rooms.map((room) =>
      room.id === updatedRoom.id ? { ...room, ...updatedRoom } : room
    );
    setRooms(updatedRooms);
    setShowCreateModal(false);
    setSelectedRoom(null);
  };

  const filteredRooms = rooms.filter((room) => {
    const stateFilter = selectedState === 'all' || room.state === selectedState;
    const priorityFilter =
      selectedPriority === 'all' || room.priority === selectedPriority;
    return stateFilter && priorityFilter;
  });

  return (
    <div style={{ padding: '20px', maxWidth: '1450px', margin: 'auto' }}>
      <h2>Room Page</h2>
      {buildingId && <h5>Building ID: {buildingId}</h5>}
      <h3>Current Temperature: {temperature !== null ? `${temperature}°C` : "Loading..."}</h3>
      <Button onClick={() => handleSetTemperature(25)}>Set Temperature to 25°C</Button>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          {selectedBuilding && buildingId && (
            <>
              <h3>Building Details</h3>
              <p>Name: {selectedBuilding.name}</p>
              <p>Description: {selectedBuilding.description}</p>
            </>
          )}
          <Button
            variant="primary"
            style={{ fontSize: "1.1rem", padding: "6px 20px", marginRight: "10px", marginLeft: "1300px", }}
            onClick={() => setShowCreateModal(true)}
          >
            Create Room
          </Button>
          <ListGroup style={{ marginTop: "30px" }}>
            {filteredRooms.map((room) => (
              <ListGroup.Item key={room.id}>
                <p>Name: {room.name}</p>
                <p>Description: {room.description}</p>
                <Button variant="info" style={{ fontSize: "1.1rem", padding: "5px 12px" }} onClick={() => handleRoomDetail(room)}>
                  Details
                </Button>
                <Button variant="danger" style={{ fontSize: "1.1rem", padding: "5px 12px" }} onClick={() => handleDeleteRoom(room.id)}>
                  Delete
                </Button>
                <Button variant="warning" style={{ fontSize: "1.1rem", padding: "5px 12px" }} onClick={() => handleEditRoom(room)}>
                  Edit
                </Button>
              </ListGroup.Item>
            ))}
          </ListGroup>

          <CreateRoomForm
            show={showCreateModal}
            handleClose={() => {
              setShowCreateModal(false);
              setSelectedRoom(null);
            }}
            handleCreateRoom={handleCreateRoom}
            handleUpdateRoom={handleUpdateRoom}
            room={selectedRoom}
          />

          {selectedRoom && (
            <RoomDetail
              room={selectedRoom}
              show={showRoomDetail}
              handleClose={handleCloseRoomDetail}
            />
          )}
        </>
      )}
    </div>
  );
};

export default RoomPage;
