import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const CreateBuilding = ({ show, handleClose, handleCreate }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [temperatureLimit, setTemperatureLimit] = useState(""); // Изначально пустое значение

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!temperatureLimit) {
      alert("Please enter a temperature limit.");
      return;
    }

    const newBuilding = {
      id: Math.floor(Math.random() * 1000), // временное решение для ID
      name,
      description,
      temperatureLimit: parseFloat(temperatureLimit), // Преобразуем в число
    };
    handleCreate(newBuilding);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Create New Building</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="buildingName">
            <Form.Label>Building Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter building name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="buildingDescription">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Enter building description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="buildingTemperatureLimit">
            <Form.Label>Temperature Limit</Form.Label>
            <Form.Control
              type="number"
              placeholder="Enter temperature limit"
              value={temperatureLimit}
              onChange={(e) => setTemperatureLimit(e.target.value)}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Create
          </Button>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateBuilding;
