import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const CreateBuilding = ({ show, handleClose, handleCreate }) => {
  const [buildingName, setBuildingName] = useState("");
  const [buildingDescription, setBuildingDescription] = useState("");
  const [temperatureLimit, setTemperatureLimit] = useState(0); // Добавляем новое состояние для лимита температуры

 const handleSubmit = (e) => {
  e.preventDefault();
  const newBuilding = {
    id: generateNewId(),  // Assuming you have a method to generate a unique ID
    name,
    description,
    temperatureLimit,  // Ensure this is captured correctly from user input
  };

  // Log the new building object to check if everything is correct
  console.log('New building created:', newBuilding);

  handleCreateBuilding(newBuilding);  // Pass the new building back to the parent
};



  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Create Building</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formBuildingName">
            <Form.Label>Building Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter building name"
              value={buildingName}
              onChange={(e) => setBuildingName(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBuildingDescription">
            <Form.Label>Building Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Enter building description"
              value={buildingDescription}
              onChange={(e) => setBuildingDescription(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formTemperatureLimit">
            <Form.Label>Temperature Limit (°C)</Form.Label>
            <Form.Control
              type="number"
              value={temperatureLimit}
              onChange={(e) => setTemperatureLimit(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateBuilding;
