import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BuildingsPage from './pages/Buildings';
import RegistrationPage from './pages/Registration';
import LoginPage from './pages/Login';
import Navigation from './components/Navigation';

function App() {
  const [user, setUser] = useState(null); // State to store the logged-in user

  return (
    <Router>
      <div className="App">
        <Navigation user={user} setUser={setUser} /> {/* Pass setUser as well */}
        <Routes>
      <Route path="/buildings/:buildingId" element={<RoomPage />} />

          <Route path="/registration" element={<RegistrationPage setUser={setUser} />} />
          <Route path="/login" element={<LoginPage setUser={setUser} />} />
          <Route path="/" element={<BuildingsPage user={user} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
