import React, { useState } from "react";
import { Button, Modal, Form } from "react-bootstrap";

const CreateBuildingForm = ({ show, handleClose, handleCreateBuilding }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [tempLimit, setTempLimit] = useState("");

  const handleSubmit = () => {
    const newBuilding = { id: Date.now(), name, description, tempLimit: parseFloat(tempLimit) };
    handleCreateBuilding(newBuilding);
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Create Building</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter building name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter building description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Temperature Limit</Form.Label>
            <Form.Control
              type="number"
              placeholder="Enter temperature limit"
              value={tempLimit}
              onChange={(e) => setTempLimit(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Create Building
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateBuildingForm;
