import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BuildingsPage from './pages/Buildings';
import RegistrationPage from './pages/Registration';
import LoginPage from './pages/Login';
import Navigation from './components/Navigation';

function App() {
  const [user, setUser] = useState(null); // State to store logged-in user info

  // Check for token in localStorage on app load to persist user session
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedUser = jwt_ecode(token);
      setUser(decodedUser); // Set the user from token if available
    }
  }, []);

  return (
    <Router>
      <div className="App">
        <Navigation user={user} setUser={setUser} /> {/* Pass user to Navigation */}
        <Routes>
          <Route path="/registration" element={<RegistrationPage setUser={setUser} />} />
          <Route path="/login" element={<LoginPage setUser={setUser} />} />
          <Route path="/" element={<BuildingsPage user={user} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
