import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const CreateBuilding = ({ show, handleClose, handleCreate }) => {
  const [buildingName, setBuildingName] = useState("");
  const [buildingDescription, setBuildingDescription] = useState("");

  const handleSubmit = () => {
    handleCreate(buildingName, buildingDescription);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Create New Building</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formBuildingName">
            <Form.Label>Building Name</Form.Label>
            <Form.Control
              type="text"
              value={buildingName}
              onChange={(e) => setBuildingName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBuildingDescription">
            <Form.Label>Building Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={buildingDescription}
              onChange={(e) => setBuildingDescription(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Create Building
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateBuilding;
